client/src/pages/admin/blog-management.t.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Plus, Edit, Trash2, Eye, Calendar } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import Layout from "@/components/Layout";
import type { BlogPost, InsertBlogPost } from "@shared/schema";

export default function BlogManagement() {
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingPost, setEditingPost] = useState<BlogPost | null>(null);
  const [formData, setFormData] = useState<InsertBlogPost>({
    title: "",
    slug: "",
    excerpt: "",
    content: "",
    imageUrl: "",
    authorId: "",
    isPublished: false,
    publishedAt: null,
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: posts, isLoading } = useQuery<BlogPost[]>({
    queryKey: ["/api/admin/blog"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: InsertBlogPost) => {
      const res = await apiRequest("POST", "/api/admin/blog", {
        ...data,
        publishedAt: data.isPublished ? new Date().toISOString() : null,
      });
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/blog"] });
      queryClient.invalidateQueries({ queryKey: ["/api/blog"] });
      toast({ title: "Blog post created successfully" });
      resetForm();
    },
    onError: (error: Error) => {
      toast({ title: "Failed to create blog post", description: error.message, variant: "destructive" });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<InsertBlogPost> }) => {
      const res = await apiRequest("PUT", `/api/admin/blog/${id}`, {
        ...data,
        publishedAt: data.isPublished ? new Date().toISOString() : null,
      });
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/blog"] });
      queryClient.invalidateQueries({ queryKey: ["/api/blog"] });
      toast({ title: "Blog post updated successfully" });
      resetForm();
    },
    onError: (error: Error) => {
      toast({ title: "Failed to update blog post", description: error.message, variant: "destructive" });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/admin/blog/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/blog"] });
      queryClient.invalidateQueries({ queryKey: ["/api/blog"] });
      toast({ title: "Blog post deleted successfully" });
    },
    onError: (error: Error) => {
      toast({ title: "Failed to delete blog post", description: error.message, variant: "destructive" });
    },
  });

  const generateSlug = (title: string) => {
    return title
      .toLowerCase()
      .replace(/[^a-z0-9 -]/g, '')
      .replace(/\s+/g, '-')
      .replace(/-+/g, '-')
      .trim();
  };

  const resetForm = () => {
    setFormData({
      title: "",
      slug: "",
      excerpt: "",
      content: "",
      imageUrl: "",
      authorId: "",
      isPublished: false,
      publishedAt: null,
    });
    setEditingPost(null);
    setIsDialogOpen(false);
  };

  const handleEdit = (post: BlogPost) => {
    setEditingPost(post);
    setFormData({
      title: post.title,
      slug: post.slug,
      excerpt: post.excerpt,
      content: post.content,
      imageUrl: post.imageUrl || "",
      authorId: post.authorId || "",
      isPublished: post.isPublished ?? false,
      publishedAt: post.publishedAt,
    });
    setIsDialogOpen(true);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (editingPost) {
      updateMutation.mutate({ id: editingPost.id, data: formData });
    } else {
      createMutation.mutate(formData);
    }
  };

  const handleDelete = (id: string) => {
    if (confirm("Are you sure you want to delete this blog post?")) {
      deleteMutation.mutate(id);
    }
  };

  const handleTitleChange = (title: string) => {
    setFormData({
      ...formData,
      title,
      slug: generateSlug(title),
    });
  };

  return (
    <Layout>
      <div className="min-h-screen bg-earth-50 py-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center mb-8">
            <div>
              <h1 className="font-serif text-4xl font-bold text-primary mb-2">Blog Management</h1>
              <p className="text-gray-600">Create and manage blog posts and articles</p>
            </div>
            <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
              <DialogTrigger asChild>
                <Button className="bg-primary hover:bg-primary/90" data-testid="button-add-post">
                  <Plus className="h-4 w-4 mr-2" />
                  Add Blog Post
                </Button>
              </DialogTrigger>
              <DialogContent className="sm:max-w-[600px] max-h-[90vh] overflow-y-auto">
                <DialogHeader>
                  <DialogTitle>
                    {editingPost ? "Edit Blog Post" : "Create Blog Post"}
                  </DialogTitle>
                  <DialogDescription>
                    {editingPost ? "Update blog post content" : "Create a new blog post for the website"}
                  </DialogDescription>
                </DialogHeader>
                <form onSubmit={handleSubmit} className="space-y-4">
                  <div>
                    <Label htmlFor="title">Title *</Label>
                    <Input
                      id="title"
                      value={formData.title}
                      onChange={(e) => handleTitleChange(e.target.value)}
                      required
                      data-testid="input-title"
                    />
                  </div>
                  <div>
                    <Label htmlFor="slug">Slug *</Label>
                    <Input
                      id="slug"
                      value={formData.slug}
                      onChange={(e) => setFormData({ ...formData, slug: e.target.value })}
                      required
                      data-testid="input-slug"
                    />
                  </div>
                  <div>
                    <Label htmlFor="excerpt">Excerpt *</Label>
                    <Textarea
                      id="excerpt"
                      value={formData.excerpt}
                      onChange={(e) => setFormData({ ...formData, excerpt: e.target.value })}
                      rows={2}
                      required
                      data-testid="textarea-excerpt"
                    />
                  </div>
                  <div>
                    <Label htmlFor="content">Content *</Label>
                    <Textarea
                      id="content"
                      value={formData.content}
                      onChange={(e) => setFormData({ ...formData, content: e.target.value })}
                      rows={8}
                      required
                      data-testid="textarea-content"
                    />
                  </div>
                  <div>
                    <Label htmlFor="imageUrl">Featured Image URL</Label>
                    <Input
                      id="imageUrl"
                      type="url"
                      value={formData.imageUrl}
                      onChange={(e) => setFormData({ ...formData, imageUrl: e.target.value })}
                      data-testid="input-image-url"
                    />
                  </div>
                  <div className="flex items-center space-x-2">
                    <Switch
                      id="isPublished"
                      checked={formData.isPublished}
                      onCheckedChange={(checked) => setFormData({ ...formData, isPublished: checked })}
                      data-testid="switch-published"
                    />
                    <Label htmlFor="isPublished">Publish immediately</Label>
                  </div>
                  <div className="flex space-x-2 pt-4">
                    <Button
                      type="submit"
                      disabled={createMutation.isPending || updateMutation.isPending}
                      data-testid="button-save-post"
                    >
                      {editingPost ? "Update" : "Create"} Post
                    </Button>
                    <Button type="button" variant="outline" onClick={resetForm} data-testid="button-cancel">
                      Cancel
                    </Button>
                  </div>
                </form>
              </DialogContent>
            </Dialog>
          </div>

          {/* Blog Posts List */}
          <div className="space-y-4">
            {posts?.map((post) => (
              <Card key={post.id} className="p-6" data-testid={`card-post-${post.id}`}>
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-center space-x-2 mb-2">
                      <h3 className="text-xl font-semibold text-primary">{post.title}</h3>
                      <span className={`px-2 py-1 text-xs rounded-full ${
                        post.isPublished 
                          ? 'bg-green-100 text-green-800' 
                          : 'bg-yellow-100 text-yellow-800'
                      }`}>
                        {post.isPublished ? 'Published' : 'Draft'}
                      </span>
                    </div>
                    <p className="text-gray-600 mb-2">{post.excerpt}</p>
                    <div className="flex items-center space-x-4 text-sm text-gray-500">
                      <span className="flex items-center">
                        <Calendar className="h-4 w-4 mr-1" />
                        {new Date(post.createdAt).toLocaleDateString()}
                      </span>
                      {post.publishedAt && (
                        <span>Published: {new Date(post.publishedAt).toLocaleDateString()}</span>
                      )}
                    </div>
                  </div>
                  <div className="flex space-x-2 ml-4">
                    {post.isPublished && (
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => window.open(`/community/blog/${post.slug}`, '_blank')}
                        data-testid={`button-view-${post.id}`}
                      >
                        <Eye className="h-4 w-4" />
                      </Button>
                    )}
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => handleEdit(post)}
                      data-testid={`button-edit-${post.id}`}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => handleDelete(post.id)}
                      disabled={deleteMutation.isPending}
                      data-testid={`button-delete-${post.id}`}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </Card>
            ))}
          </div>

          {!posts?.length && !isLoading && (
            <div className="text-center py-12">
              <p className="text-gray-500 text-lg">No blog posts found. Create your first post!</p>
            </div>
          )}
        </div>
      </div>
    </Layout>
  );
}

client/src/pages/admin/events-management.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Switch } from "@/components/ui/switch";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Plus, Edit, Trash2, Calendar, MapPin, Users } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import Layout from "@/components/Layout";
import type { Event, InsertEvent } from "@shared/schema";

export default function EventsManagement() {
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingEvent, setEditingEvent] = useState<Event | null>(null);
  const [formData, setFormData] = useState<InsertEvent>({
    title: "",
    description: "",
    imageUrl: "",
    location: "",
    startDate: new Date(),
    endDate: null,
    isPublic: true,
    maxAttendees: null,
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: events, isLoading } = useQuery<Event[]>({
    queryKey: ["/api/admin/events"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: InsertEvent) => {
      const res = await apiRequest("POST", "/api/admin/events", data);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/events"] });
      queryClient.invalidateQueries({ queryKey: ["/api/events"] });
      toast({ title: "Event created successfully" });
      resetForm();
    },
    onError: (error: Error) => {
      toast({ title: "Failed to create event", description: error.message, variant: "destructive" });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<InsertEvent> }) => {
      const res = await apiRequest("PUT", `/api/admin/events/${id}`, data);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/events"] });
      queryClient.invalidateQueries({ queryKey: ["/api/events"] });
      toast({ title: "Event updated successfully" });
      resetForm();
    },
    onError: (error: Error) => {
      toast({ title: "Failed to update event", description: error.message, variant: "destructive" });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/admin/events/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/events"] });
      queryClient.invalidateQueries({ queryKey: ["/api/events"] });
      toast({ title: "Event deleted successfully" });
    },
    onError: (error: Error) => {
      toast({ title: "Failed to delete event", description: error.message, variant: "destructive" });
    },
  });

  const resetForm = () => {
    setFormData({
      title: "",
      description: "",
      imageUrl: "",
      location: "",
      startDate: new Date(),
      endDate: null,
      isPublic: true,
      maxAttendees: null,
    });
    setEditingEvent(null);
    setIsDialogOpen(false);
  };

  const handleEdit = (event: Event) => {
    setEditingEvent(event);
    setFormData({
      title: event.title,
      description: event.description,
      imageUrl: event.imageUrl || "",
      location: event.location || "",
      startDate: new Date(event.startDate),
      endDate: event.endDate ? new Date(event.endDate) : null,
      isPublic: event.isPublic ?? true,
      maxAttendees: event.maxAttendees,
    });
    setIsDialogOpen(true);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (editingEvent) {
      updateMutation.mutate({ id: editingEvent.id, data: formData });
    } else {
      createMutation.mutate(formData);
    }
  };

  const handleDelete = (id: string) => {
    if (confirm("Are you sure you want to delete this event?")) {
      deleteMutation.mutate(id);
    }
  };

  const formatDateTime = (date: Date) => {
    return date.toISOString().slice(0, 16);
  };

  return (
    <Layout>
      <div className="min-h-screen bg-earth-50 py-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center mb-8">
            <div>
              <h1 className="font-serif text-4xl font-bold text-primary mb-2">Events Management</h1>
              <p className="text-gray-600">Create and manage community events</p>
            </div>
            <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
              <DialogTrigger asChild>
                <Button className="bg-primary hover:bg-primary/90" data-testid="button-add-event">
                  <Plus className="h-4 w-4 mr-2" />
                  Add Event
                </Button>
              </DialogTrigger>
              <DialogContent className="sm:max-w-[500px] max-h-[90vh] overflow-y-auto">
                <DialogHeader>
                  <DialogTitle>
                    {editingEvent ? "Edit Event" : "Create Event"}
                  </DialogTitle>
                  <DialogDescription>
                    {editingEvent ? "Update event details" : "Create a new community event"}
                  </DialogDescription>
                </DialogHeader>
                <form onSubmit={handleSubmit} className="space-y-4">
                  <div>
                    <Label htmlFor="title">Event Title *</Label>
                    <Input
                      id="title"
                      value={formData.title}
                      onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                      required
                      data-testid="input-title"
                    />
                  </div>
                  <div>
                    <Label htmlFor="description">Description *</Label>
                    <Textarea
                      id="description"
                      value={formData.description}
                      onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                      rows={3}
                      required
                      data-testid="textarea-description"
                    />
                  </div>
                  <div>
                    <Label htmlFor="location">Location</Label>
                    <Input
                      id="location"
                      value={formData.location}
                      onChange={(e) => setFormData({ ...formData, location: e.target.value })}
                      data-testid="input-location"
                    />
                  </div>
                  <div>
                    <Label htmlFor="imageUrl">Event Image URL</Label>
                    <Input
                      id="imageUrl"
                      type="url"
                      value={formData.imageUrl}
                      onChange={(e) => setFormData({ ...formData, imageUrl: e.target.value })}
                      data-testid="input-image-url"
                    />
                  </div>
                  <div>
                    <Label htmlFor="startDate">Start Date & Time *</Label>
                    <Input
                      id="startDate"
                      type="datetime-local"
                      value={formatDateTime(formData.startDate)}
                      onChange={(e) => setFormData({ ...formData, startDate: new Date(e.target.value) })}
                      required
                      data-testid="input-start-date"
                    />
                  </div>
                  <div>
                    <Label htmlFor="endDate">End Date & Time</Label>
                    <Input
                      id="endDate"
                      type="datetime-local"
                      value={formData.endDate ? formatDateTime(formData.endDate) : ""}
                      onChange={(e) => setFormData({ 
                        ...formData, 
                        endDate: e.target.value ? new Date(e.target.value) : null 
                      })}
                      data-testid="input-end-date"
                    />
                  </div>
                  <div>
                    <Label htmlFor="maxAttendees">Max Attendees</Label>
                    <Input
                      id="maxAttendees"
                      type="number"
                      min="1"
                      value={formData.maxAttendees || ""}
                      onChange={(e) => setFormData({ 
                        ...formData, 
                        maxAttendees: e.target.value ? parseInt(e.target.value) : null 
                      })}
                      data-testid="input-max-attendees"
                    />
                  </div>
                  <div className="flex items-center space-x-2">
                    <Switch
                      id="isPublic"
                      checked={formData.isPublic}
                      onCheckedChange={(checked) => setFormData({ ...formData, isPublic: checked })}
                      data-testid="switch-public"
                    />
                    <Label htmlFor="isPublic">Public event (visible to visitors)</Label>
                  </div>
                  <div className="flex space-x-2 pt-4">
                    <Button
                      type="submit"
                      disabled={createMutation.isPending || updateMutation.isPending}
                      data-testid="button-save-event"
                    >
                      {editingEvent ? "Update" : "Create"} Event
                    </Button>
                    <Button type="button" variant="outline" onClick={resetForm} data-testid="button-cancel">
                      Cancel
                    </Button>
                  </div>
                </form>
              </DialogContent>
            </Dialog>
          </div>

          {/* Events Grid */}
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
            {events?.map((event) => (
              <Card key={event.id} className="overflow-hidden" data-testid={`card-event-${event.id}`}>
                {event.imageUrl && (
                  <div className="h-48 overflow-hidden">
                    <img
                      src={event.imageUrl}
                      alt={event.title}
                      className="w-full h-full object-cover"
                    />
                  </div>
                )}
                <CardHeader>
                  <div className="flex items-start justify-between">
                    <CardTitle className="text-lg">{event.title}</CardTitle>
                    <span className={`px-2 py-1 text-xs rounded-full ${
                      event.isPublic 
                        ? 'bg-green-100 text-green-800' 
                        : 'bg-gray-100 text-gray-800'
                    }`}>
                      {event.isPublic ? 'Public' : 'Private'}
                    </span>
                  </div>
                </CardHeader>
                <CardContent>
                  <p className="text-sm text-gray-600 mb-4 line-clamp-2">{event.description}</p>

                  <div className="space-y-2 text-sm text-gray-500 mb-4">
                    <div className="flex items-center">
                      <Calendar className="h-4 w-4 mr-2" />
                      <span>{new Date(event.startDate).toLocaleDateString()}</span>
                    </div>
                    {event.location && (
                      <div className="flex items-center">
                        <MapPin className="h-4 w-4 mr-2" />
                        <span>{event.location}</span>
                      </div>
                    )}
                    {event.maxAttendees && (
                      <div className="flex items-center">
                        <Users className="h-4 w-4 mr-2" />
                        <span>Max {event.maxAttendees} attendees</span>
                      </div>
                    )}
                  </div>

                  <div className="flex space-x-2">
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => handleEdit(event)}
                      data-testid={`button-edit-${event.id}`}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button
                      size="sm"
                      variant="outline"
                      onClick={() => handleDelete(event.id)}
                      disabled={deleteMutation.isPending}
                      data-testid={`button-delete-${event.id}`}
                    >
                      <Trash2 className="h-4 w-4" />
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>

          {!events?.length && !isLoading && (
            <div className="text-center py-12">
              <p className="text-gray-500 text-lg">No events found. Create your first event!</p>
            </div>
          )}
        </div>
      </div>
    </Layout>
  );
}

client/src/pages/admin/team-management.t.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Plus, Edit, Trash2, Linkedin, Twitter, Mail } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import Layout from "@/components/Layout";
import type { TeamMember, InsertTeamMember } from "@shared/schema";

export default function TeamManagement() {
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingMember, setEditingMember] = useState<TeamMember | null>(null);
  const [formData, setFormData] = useState<InsertTeamMember>({
    name: "",
    role: "",
    bio: "",
    imageUrl: "",
    email: "",
    linkedinUrl: "",
    twitterUrl: "",
    isActive: true,
  });

  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: team, isLoading } = useQuery<TeamMember[]>({
    queryKey: ["/api/admin/team"],
  });

  const createMutation = useMutation({
    mutationFn: async (data: InsertTeamMember) => {
      const res = await apiRequest("POST", "/api/admin/team", data);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/team"] });
      queryClient.invalidateQueries({ queryKey: ["/api/team"] });
      toast({ title: "Team member added successfully" });
      resetForm();
    },
    onError: (error: Error) => {
      toast({ title: "Failed to add team member", description: error.message, variant: "destructive" });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<InsertTeamMember> }) => {
      const res = await apiRequest("PUT", `/api/admin/team/${id}`, data);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/team"] });
      queryClient.invalidateQueries({ queryKey: ["/api/team"] });
      toast({ title: "Team member updated successfully" });
      resetForm();
    },
    onError: (error: Error) => {
      toast({ title: "Failed to update team member", description: error.message, variant: "destructive" });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/admin/team/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/team"] });
      queryClient.invalidateQueries({ queryKey: ["/api/team"] });
      toast({ title: "Team member deleted successfully" });
    },
    onError: (error: Error) => {
      toast({ title: "Failed to delete team member", description: error.message, variant: "destructive" });
    },
  });

  const resetForm = () => {
    setFormData({
      name: "",
      role: "",
      bio: "",
      imageUrl: "",
      email: "",
      linkedinUrl: "",
      twitterUrl: "",
      isActive: true,
    });
    setEditingMember(null);
    setIsDialogOpen(false);
  };

  const handleEdit = (member: TeamMember) => {
    setEditingMember(member);
    setFormData({
      name: member.name,
      role: member.role,
      bio: member.bio,
      imageUrl: member.imageUrl || "",
      email: member.email || "",
      linkedinUrl: member.linkedinUrl || "",
      twitterUrl: member.twitterUrl || "",
      isActive: member.isActive ?? true,
    });
    setIsDialogOpen(true);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (editingMember) {
      updateMutation.mutate({ id: editingMember.id, data: formData });
    } else {
      createMutation.mutate(formData);
    }
  };

  const handleDelete = (id: string) => {
    if (confirm("Are you sure you want to delete this team member?")) {
      deleteMutation.mutate(id);
    }
  };

  return (
    <Layout>
      <div className="min-h-screen bg-earth-50 py-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center mb-8">
            <div>
              <h1 className="font-serif text-4xl font-bold text-primary mb-2">Team Management</h1>
              <p className="text-gray-600">Manage team member profiles and information</p>
            </div>
            <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
              <DialogTrigger asChild>
                <Button className="bg-primary hover:bg-primary/90" data-testid="button-add-member">
                  <Plus className="h-4 w-4 mr-2" />
                  Add Team Member
                </Button>
              </DialogTrigger>
              <DialogContent className="sm:max-w-[500px] max-h-[90vh] overflow-y-auto">
                <DialogHeader>
                  <DialogTitle>
                    {editingMember ? "Edit Team Member" : "Add Team Member"}
                  </DialogTitle>
                  <DialogDescription>
                    {editingMember ? "Update team member information" : "Add a new team member to the website"}
                  </DialogDescription>
                </DialogHeader>
                <form onSubmit={handleSubmit} className="space-y-4">
                  <div>
                    <Label htmlFor="name">Name *</Label>
                    <Input
                      id="name"
                      value={formData.name}
                      onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                      required
                      data-testid="input-name"
                    />
                  </div>
                  <div>
                    <Label htmlFor="role">Role *</Label>
                    <Input
                      id="role"
                      value={formData.role}
                      onChange={(e) => setFormData({ ...formData, role: e.target.value })}
                      required
                      data-testid="input-role"
                    />
                  </div>
                  <div>
                    <Label htmlFor="bio">Bio *</Label>
                    <Textarea
                      id="bio"
                      value={formData.bio}
                      onChange={(e) => setFormData({ ...formData, bio: e.target.value })}
                      rows={3}
                      required
                      data-testid="textarea-bio"
                    />
                  </div>
                  <div>
                    <Label htmlFor="imageUrl">Image URL</Label>
                    <Input
                      id="imageUrl"
                      type="url"
                      value={formData.imageUrl}
                      onChange={(e) => setFormData({ ...formData, imageUrl: e.target.value })}
                      data-testid="input-image-url"
                    />
                  </div>
                  <div>
                    <Label htmlFor="email">Email</Label>
                    <Input
                      id="email"
                      type="email"
                      value={formData.email}
                      onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                      data-testid="input-email"
                    />
                  </div>
                  <div>
                    <Label htmlFor="linkedinUrl">LinkedIn URL</Label>
                    <Input
                      id="linkedinUrl"
                      type="url"
                      value={formData.linkedinUrl}
                      onChange={(e) => setFormData({ ...formData, linkedinUrl: e.target.value })}
                      data-testid="input-linkedin"
                    />
                  </div>
                  <div>
                    <Label htmlFor="twitterUrl">Twitter URL</Label>
                    <Input
                      id="twitterUrl"
                      type="url"
                      value={formData.twitterUrl}
                      onChange={(e) => setFormData({ ...formData, twitterUrl: e.target.value })}
                      data-testid="input-twitter"
                    />
                  </div>
                  <div className="flex space-x-2 pt-4">
                    <Button
                      type="submit"
                      disabled={createMutation.isPending || updateMutation.isPending}
                      data-testid="button-save-member"
                    >
                      {editingMember ? "Update" : "Add"} Member
                    </Button>
                    <Button type="button" variant="outline" onClick={resetForm} data-testid="button-cancel">
                      Cancel
                    </Button>
                  </div>
                </form>
              </DialogContent>
            </Dialog>
          </div>

          {/* Team Members Grid */}
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
            {team?.map((member) => (
              <Card key={member.id} className="overflow-hidden" data-testid={`card-member-${member.id}`}>
                {member.imageUrl && (
                  <div className="h-48 overflow-hidden">
                    <img
                      src={member.imageUrl}
                      alt={member.name}
                      className="w-full h-full object-cover"
                    />
                  </div>
                )}
                <CardHeader>
                  <CardTitle className="text-lg">{member.name}</CardTitle>
                  <CardDescription className="text-accent font-semibold">
                    {member.role}
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <p className="text-sm text-gray-600 mb-4 line-clamp-3">{member.bio}</p>

                  <div className="flex items-center justify-between">
                    <div className="flex space-x-2">
                      {member.linkedinUrl && (
                        <a href={member.linkedinUrl} target="_blank" rel="noopener noreferrer">
                          <Linkedin className="h-4 w-4 text-gray-400 hover:text-primary" />
                        </a>
                      )}
                      {member.twitterUrl && (
                        <a href={member.twitterUrl} target="_blank" rel="noopener noreferrer">
                          <Twitter className="h-4 w-4 text-gray-400 hover:text-primary" />
                        </a>
                      )}
                      {member.email && (
                        <a href={`mailto:${member.email}`}>
                          <Mail className="h-4 w-4 text-gray-400 hover:text-primary" />
                        </a>
                      )}
                    </div>

                    <div className="flex space-x-2">
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => handleEdit(member)}
                        data-testid={`button-edit-${member.id}`}
                      >
                        <Edit className="h-4 w-4" />
                      </Button>
                      <Button
                        size="sm"
                        variant="outline"
                        onClick={() => handleDelete(member.id)}
                        disabled={deleteMutation.isPending}
                        data-testid={`button-delete-${member.id}`}
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>

                  <div className="mt-2">
                    <span className={`inline-block px-2 py-1 text-xs rounded-full ${
                      member.isActive 
                        ? 'bg-green-100 text-green-800' 
                        : 'bg-gray-100 text-gray-800'
                    }`}>
                      {member.isActive ? 'Active' : 'Inactive'}
                    </span>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>

          {!team?.length && !isLoading && (
            <div className="text-center py-12">
              <p className="text-gray-500 text-lg">No team members found. Add your first team member!</p>
            </div>
          )}
        </div>
      </div>
    </Layout>
  );
}

client/src/pages/community/blog.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Calendar, User, ArrowRight, Search } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import Layout from "@/components/Layout";
import { useState } from "react";
import { Link } from "wouter";
import type { BlogPost } from "@shared/schema";

export default function BlogPage() {
  const [searchTerm, setSearchTerm] = useState("");

  const { data: posts, isLoading, error } = useQuery<BlogPost[]>({
    queryKey: ["/api/blog"],
  });

  const filteredPosts = posts?.filter(post => 
    post.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    post.excerpt.toLowerCase().includes(searchTerm.toLowerCase())
  ) || [];

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const featuredPost = filteredPosts[0];
  const otherPosts = filteredPosts.slice(1);

  return (
    <Layout>
      <div className="min-h-screen bg-earth-50 py-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {/* Hero Section */}
          <div className="text-center mb-16">
            <h1 className="font-serif text-4xl md:text-5xl font-bold text-primary mb-6">Stories & Features</h1>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto leading-relaxed">
              Discover inspiring stories, program updates, and insights from our community. 
              Read about the impact we're making together and the voices we're amplifying across Kenya.
            </p>
          </div>

          {/* Search Bar */}
          <div className="max-w-md mx-auto mb-12">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
              <Input
                type="text"
                placeholder="Search articles..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="pl-10"
                data-testid="input-search-blog"
              />
            </div>
          </div>

          {/* Error State */}
          {error && (
            <div className="text-center py-12">
              <div className="bg-red-50 border border-red-200 rounded-lg p-6 max-w-md mx-auto">
                <h3 className="text-lg font-semibold text-red-800 mb-2">Unable to Load Articles</h3>
                <p className="text-red-600">
                  We're having trouble loading our blog posts. Please try again later or contact us if the problem persists.
                </p>
              </div>
            </div>
          )}

          {/* Loading State */}
          {isLoading && (
            <div className="space-y-8">
              {/* Featured post skeleton */}
              <Card className="overflow-hidden">
                <div className="md:flex">
                  <Skeleton className="md:w-1/2 h-64 md:h-80" />
                  <div className="md:w-1/2 p-8">
                    <Skeleton className="h-8 w-48 mb-4" />
                    <Skeleton className="h-4 w-full mb-2" />
                    <Skeleton className="h-4 w-3/4 mb-6" />
                    <Skeleton className="h-10 w-32" />
                  </div>
                </div>
              </Card>

              {/* Other posts skeleton */}
              <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                {[...Array(6)].map((_, i) => (
                  <Card key={i} className="overflow-hidden">
                    <Skeleton className="h-48 w-full" />
                    <div className="p-6">
                      <Skeleton className="h-6 w-32 mb-3" />
                      <Skeleton className="h-4 w-full mb-2" />
                      <Skeleton className="h-4 w-3/4 mb-4" />
                      <Skeleton className="h-8 w-24" />
                    </div>
                  </Card>
                ))}
              </div>
            </div>
          )}

          {/* Blog Content */}
          {!isLoading && !error && (
            <>
              {/* Featured Post */}
              {featuredPost && (
                <div className="mb-16">
                  <h2 className="font-serif text-2xl font-bold text-primary mb-6">Featured Story</h2>
                  <Card className="overflow-hidden hover:shadow-xl transition-shadow duration-300" data-testid="card-featured-post">
                    <div className="md:flex">
                      {featuredPost.imageUrl && (
                        <div className="md:w-1/2 h-64 md:h-80 overflow-hidden">
                          <img 
                            src={featuredPost.imageUrl} 
                            alt={featuredPost.title}
                            className="w-full h-full object-cover"
                          />
                        </div>
                      )}
                      <div className={`${featuredPost.imageUrl ? 'md:w-1/2' : 'w-full'} p-8`}>
                        <div className="flex items-center text-sm text-gray-500 mb-4">
                          <Calendar className="h-4 w-4 mr-2" />
                          <span>{formatDate(featuredPost.publishedAt || featuredPost.createdAt)}</span>
                          <User className="h-4 w-4 ml-4 mr-2" />
                          <span>VOH-CBO Team</span>
                        </div>
                        <h3 className="font-serif text-2xl md:text-3xl font-bold text-primary mb-4">
                          {featuredPost.title}
                        </h3>
                        <p className="text-gray-600 mb-6 leading-relaxed">
                          {featuredPost.excerpt}
                        </p>
                        <Link href={`/community/blog/${featuredPost.slug}`}>
                          <Button className="bg-accent hover:bg-orange-600" data-testid={`button-read-featured-${featuredPost.id}`}>
                            Read Full Story
                            <ArrowRight className="h-4 w-4 ml-2" />
                          </Button>
                        </Link>
                      </div>
                    </div>
                  </Card>
                </div>
              )}

              {/* Other Posts */}
              {otherPosts.length > 0 && (
                <div className="mb-16">
                  <h2 className="font-serif text-2xl font-bold text-primary mb-6">
                    {featuredPost ? 'More Stories' : 'Latest Stories'}
                  </h2>
                  <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {otherPosts.map((post) => (
                      <Card key={post.id} className="overflow-hidden hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1" data-testid={`card-blog-post-${post.id}`}>
                        {post.imageUrl && (
                          <div className="h-48 overflow-hidden">
                            <img 
                              src={post.imageUrl} 
                              alt={post.title}
                              className="w-full h-full object-cover"
                            />
                          </div>
                        )}
                        <CardHeader>
                          <div className="flex items-center text-sm text-gray-500 mb-2">
                            <Calendar className="h-4 w-4 mr-2" />
                            <span>{formatDate(post.publishedAt || post.createdAt)}</span>
                          </div>
                          <CardTitle className="text-lg text-primary line-clamp-2">
                            {post.title}
                          </CardTitle>
                        </CardHeader>
                        <CardContent>
                          <p className="text-gray-600 mb-4 line-clamp-3">
                            {post.excerpt}
                          </p>
                          <Link href={`/community/blog/${post.slug}`}>
                            <Button variant="outline" className="w-full" data-testid={`button-read-more-${post.id}`}>
                              Read More
                              <ArrowRight className="h-4 w-4 ml-2" />
                            </Button>
                          </Link>
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                </div>
              )}

              {/* No Results */}
              {searchTerm && filteredPosts.length === 0 && posts && posts.length > 0 && (
                <div className="text-center py-16">
                  <Search className="h-24 w-24 text-gray-300 mx-auto mb-6" />
                  <h3 className="text-2xl font-semibold text-gray-600 mb-4">No Results Found</h3>
                  <p className="text-gray-500 mb-8 max-w-md mx-auto">
                    We couldn't find any articles matching "{searchTerm}". Try different keywords or browse all our stories.
                  </p>
                  <Button 
                    onClick={() => setSearchTerm("")} 
                    className="bg-primary hover:bg-primary/90"
                    data-testid="button-clear-search"
                  >
                    Clear Search
                  </Button>
                </div>
              )}

              {/* Empty State */}
              {posts && posts.length === 0 && (
                <div className="text-center py-16">
                  <div className="w-24 h-24 bg-gray-200 rounded-full flex items-center justify-center mx-auto mb-6">
                    <User className="h-12 w-12 text-gray-400" />
                  </div>
                  <h3 className="text-2xl font-semibold text-gray-600 mb-4">No Stories Yet</h3>
                  <p className="text-gray-500 mb-8 max-w-md mx-auto">
                    We're working on bringing you inspiring stories and updates from our community. 
                    Check back soon for our latest features and impact stories.
                  </p>
                  <div className="flex flex-col sm:flex-row gap-4 justify-center">
                    <Button className="bg-primary hover:bg-primary/90" data-testid="button-contact-blog">
                      Contact Us
                    </Button>
                    <Button variant="outline" data-testid="button-newsletter-blog">
                      Subscribe for Updates
                    </Button>
                  </div>
                </div>
              )}
            </>
          )}

          {/* Call to Action */}
          {posts && posts.length > 0 && (
            <div className="bg-gradient-to-br from-secondary/10 to-accent/10 rounded-2xl p-12 text-center">
              <h2 className="font-serif text-3xl font-bold text-primary mb-4">Share Your Story</h2>
              <p className="text-xl text-gray-600 mb-8 max-w-2xl mx-auto">
                Have an inspiring story or experience with VOH-CBO? We'd love to hear from you and 
                share your journey with our community.
              </p>
              <div className="flex flex-col sm:flex-row gap-4 justify-center">
                <Button size="lg" className="bg-accent hover:bg-orange-600" data-testid="button-share-story">
                  Share Your Story
                </Button>
                <Button 
                  size="lg" 
                  variant="outline"
                  data-testid="button-newsletter-signup-blog"
                >
                  Subscribe to Newsletter
                </Button>
              </div>
            </div>
          )}
        </div>
      </div>
    </Layout>
  );
}

client/src/pages/community/events.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Calendar, MapPin, Users, Clock, ChevronRight } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import Layout from "@/components/Layout";
import type { Event } from "@shared/schema";

export default function EventsPage() {
  const { data: events, isLoading, error } = useQuery<Event[]>({
    queryKey: ["/api/events"],
  });

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const formatTime = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const isUpcoming = (dateString: string) => {
    return new Date(dateString) > new Date();
  };

  const upcomingEvents = events?.filter(event => isUpcoming(event.startDate)) || [];
  const pastEvents = events?.filter(event => !isUpcoming(event.startDate)) || [];

  return (
    <Layout>
      <div className="min-h-screen bg-earth-50 py-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {/* Hero Section */}
          <div className="text-center mb-16">
            <h1 className="font-serif text-4xl md:text-5xl font-bold text-primary mb-6">Community Events</h1>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto leading-relaxed">
              Join us for meaningful events that bring our community together, celebrate achievements, 
              and create opportunities for learning, networking, and empowerment.
            </p>
          </div>

          {/* Error State */}
          {error && (
            <div className="text-center py-12">
              <div className="bg-red-50 border border-red-200 rounded-lg p-6 max-w-md mx-auto">
                <h3 className="text-lg font-semibold text-red-800 mb-2">Unable to Load Events</h3>
                <p className="text-red-600">
                  We're having trouble loading our events. Please try again later or contact us if the problem persists.
                </p>
              </div>
            </div>
          )}

          {/* Loading State */}
          {isLoading && (
            <div className="space-y-8">
              <div>
                <Skeleton className="h-8 w-48 mb-6" />
                <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {[...Array(6)].map((_, i) => (
                    <Card key={i} className="overflow-hidden">
                      <Skeleton className="h-48 w-full" />
                      <div className="p-6">
                        <Skeleton className="h-6 w-32 mb-3" />
                        <Skeleton className="h-4 w-full mb-2" />
                        <Skeleton className="h-4 w-3/4 mb-4" />
                        <div className="space-y-2">
                          <Skeleton className="h-4 w-28" />
                          <Skeleton className="h-4 w-32" />
                        </div>
                      </div>
                    </Card>
                  ))}
                </div>
              </div>
            </div>
          )}

          {/* Events Content */}
          {!isLoading && !error && (
            <>
              {/* Upcoming Events */}
              {upcomingEvents.length > 0 && (
                <div className="mb-16">
                  <h2 className="font-serif text-3xl font-bold text-primary mb-8">Upcoming Events</h2>
                  <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {upcomingEvents.map((event) => (
                      <Card key={event.id} className="overflow-hidden hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1" data-testid={`card-upcoming-event-${event.id}`}>
                        {event.imageUrl && (
                          <div className="h-48 overflow-hidden">
                            <img 
                              src={event.imageUrl} 
                              alt={event.title}
                              className="w-full h-full object-cover"
                            />
                          </div>
                        )}
                        <CardHeader>
                          <div className="flex items-start justify-between">
                            <CardTitle className="text-lg text-primary pr-2">{event.title}</CardTitle>
                            <Badge className="bg-green-100 text-green-800 hover:bg-green-100">
                              Upcoming
                            </Badge>
                          </div>
                        </CardHeader>
                        <CardContent>
                          <p className="text-gray-600 mb-4 line-clamp-2">{event.description}</p>

                          <div className="space-y-2 text-sm text-gray-500 mb-4">
                            <div className="flex items-center">
                              <Calendar className="h-4 w-4 mr-2 text-accent" />
                              <span>{formatDate(event.startDate)}</span>
                            </div>
                            <div className="flex items-center">
                              <Clock className="h-4 w-4 mr-2 text-accent" />
                              <span>{formatTime(event.startDate)}</span>
                            </div>
                            {event.location && (
                              <div className="flex items-center">
                                <MapPin className="h-4 w-4 mr-2 text-accent" />
                                <span>{event.location}</span>
                              </div>
                            )}
                            {event.maxAttendees && (
                              <div className="flex items-center">
                                <Users className="h-4 w-4 mr-2 text-accent" />
                                <span>Max {event.maxAttendees} attendees</span>
                              </div>
                            )}
                          </div>

                          <Button className="w-full bg-accent hover:bg-orange-600" data-testid={`button-register-${event.id}`}>
                            Register Interest
                            <ChevronRight className="h-4 w-4 ml-2" />
                          </Button>
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                </div>
              )}

              {/* Past Events */}
              {pastEvents.length > 0 && (
                <div className="mb-16">
                  <h2 className="font-serif text-3xl font-bold text-primary mb-8">Past Events</h2>
                  <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {pastEvents.slice(0, 6).map((event) => (
                      <Card key={event.id} className="overflow-hidden hover:shadow-lg transition-shadow duration-300" data-testid={`card-past-event-${event.id}`}>
                        {event.imageUrl && (
                          <div className="h-48 overflow-hidden">
                            <img 
                              src={event.imageUrl} 
                              alt={event.title}
                              className="w-full h-full object-cover opacity-75"
                            />
                          </div>
                        )}
                        <CardHeader>
                          <div className="flex items-start justify-between">
                            <CardTitle className="text-lg text-primary pr-2">{event.title}</CardTitle>
                            <Badge variant="outline" className="text-gray-500">
                              Past Event
                            </Badge>
                          </div>
                        </CardHeader>
                        <CardContent>
                          <p className="text-gray-600 mb-4 line-clamp-2">{event.description}</p>

                          <div className="space-y-2 text-sm text-gray-500">
                            <div className="flex items-center">
                              <Calendar className="h-4 w-4 mr-2 text-gray-400" />
                              <span>{formatDate(event.startDate)}</span>
                            </div>
                            {event.location && (
                              <div className="flex items-center">
                                <MapPin className="h-4 w-4 mr-2 text-gray-400" />
                                <span>{event.location}</span>
                              </div>
                            )}
                          </div>
                        </CardContent>
                      </Card>
                    ))}
                  </div>

                  {pastEvents.length > 6 && (
                    <div className="text-center mt-8">
                      <Button variant="outline" data-testid="button-view-more-past">
                        View More Past Events
                      </Button>
                    </div>
                  )}
                </div>
              )}

              {/* Empty State */}
              {events && events.length === 0 && (
                <div className="text-center py-16">
                  <Calendar className="h-24 w-24 text-gray-300 mx-auto mb-6" />
                  <h3 className="text-2xl font-semibold text-gray-600 mb-4">No Events Found</h3>
                  <p className="text-gray-500 mb-8 max-w-md mx-auto">
                    We don't have any events scheduled at the moment. Check back soon for upcoming 
                    community gatherings, workshops, and celebrations.
                  </p>
                  <div className="flex flex-col sm:flex-row gap-4 justify-center">
                    <Button className="bg-primary hover:bg-primary/90" data-testid="button-contact-events">
                      Contact Us for Updates
                    </Button>
                    <Button variant="outline" data-testid="button-newsletter-signup">
                      Subscribe to Newsletter
                    </Button>
                  </div>
                </div>
              )}
            </>
          )}

          {/* Call to Action */}
          {events && events.length > 0 && (
            <div className="bg-gradient-to-r from-primary to-secondary rounded-2xl p-12 text-white text-center">
              <h2 className="font-serif text-3xl font-bold mb-4">Stay Connected</h2>
              <p className="text-xl opacity-90 mb-8 max-w-2xl mx-auto">
                Don't miss out on our upcoming events! Subscribe to our newsletter to get the latest 
                updates on workshops, celebrations, and community gatherings.
              </p>
              <div className="flex flex-col sm:flex-row gap-4 justify-center">
                <Button size="lg" className="bg-white text-primary hover:bg-gray-100" data-testid="button-newsletter-cta">
                  Subscribe to Newsletter
                </Button>
                <Button 
                  size="lg" 
                  variant="outline" 
                  className="border-white text-white hover:bg-white hover:text-primary"
                  data-testid="button-contact-cta"
                >
                  Contact Us
                </Button>
              </div>
            </div>
          )}
        </div>
      </div>
    </Layout>
  );
}

client/src/pages/community/shop.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { ShoppingCart, Star, Heart, Package } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import Layout from "@/components/Layout";
import { useState } from "react";
import type { Merchandise } from "@shared/schema";

export default function ShopPage() {
  const [cart, setCart] = useState<{[key: string]: number}>({});

  const { data: merchandise, isLoading, error } = useQuery<Merchandise[]>({
    queryKey: ["/api/merchandise"],
  });

  const addToCart = (itemId: string) => {
    setCart(prev => ({
      ...prev,
      [itemId]: (prev[itemId] || 0) + 1
    }));
  };

  const getCartItemCount = (itemId: string) => cart[itemId] || 0;
  const getTotalItems = () => Object.values(cart).reduce((sum, count) => sum + count, 0);

  const categories = [...new Set(merchandise?.map(item => item.category) || [])];

  return (
    <Layout>
      <div className="min-h-screen bg-earth-50 py-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {/* Hero Section */}
          <div className="text-center mb-16">
            <h1 className="font-serif text-4xl md:text-5xl font-bold text-primary mb-6">VOH-CBO Shop</h1>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto leading-relaxed">
              Support our mission while showing your commitment to empowering women and girls. 
              Every purchase helps fund our programs and creates lasting change in communities across Kenya.
            </p>

            {/* Cart Summary */}
            {getTotalItems() > 0 && (
              <div className="bg-white rounded-lg p-4 max-w-md mx-auto mt-8 shadow-lg">
                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <ShoppingCart className="h-5 w-5 text-accent mr-2" />
                    <span className="font-semibold">Cart: {getTotalItems()} items</span>
                  </div>
                  <Button size="sm" className="bg-accent hover:bg-orange-600" data-testid="button-view-cart">
                    View Cart
                  </Button>
                </div>
              </div>
            )}
          </div>

          {/* Error State */}
          {error && (
            <div className="text-center py-12">
              <div className="bg-red-50 border border-red-200 rounded-lg p-6 max-w-md mx-auto">
                <h3 className="text-lg font-semibold text-red-800 mb-2">Unable to Load Shop</h3>
                <p className="text-red-600">
                  We're having trouble loading our merchandise. Please try again later or contact us if the problem persists.
                </p>
              </div>
            </div>
          )}

          {/* Loading State */}
          {isLoading && (
            <div className="grid md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
              {[...Array(8)].map((_, i) => (
                <Card key={i} className="overflow-hidden">
                  <Skeleton className="h-64 w-full" />
                  <div className="p-6">
                    <Skeleton className="h-6 w-32 mb-2" />
                    <Skeleton className="h-4 w-full mb-2" />
                    <Skeleton className="h-4 w-3/4 mb-4" />
                    <Skeleton className="h-6 w-20 mb-4" />
                    <Skeleton className="h-10 w-full" />
                  </div>
                </Card>
              ))}
            </div>
          )}

          {/* Shop Content */}
          {!isLoading && !error && (
            <>
              {/* Category Filter */}
              {categories.length > 1 && (
                <div className="mb-8">
                  <div className="flex flex-wrap justify-center gap-2">
                    <Badge variant="outline" className="cursor-pointer hover:bg-primary hover:text-white">
                      All Items
                    </Badge>
                    {categories.map((category) => (
                      <Badge 
                        key={category} 
                        variant="outline" 
                        className="cursor-pointer hover:bg-primary hover:text-white capitalize"
                        data-testid={`filter-${category}`}
                      >
                        {category}
                      </Badge>
                    ))}
                  </div>
                </div>
              )}

              {/* Merchandise Grid */}
              {merchandise && merchandise.length > 0 && (
                <div className="grid md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6 mb-16">
                  {merchandise.map((item) => (
                    <Card key={item.id} className="overflow-hidden hover:shadow-xl transition-all duration-300 transform hover:-translate-y-1" data-testid={`card-product-${item.id}`}>
                      {item.imageUrl && (
                        <div className="h-64 overflow-hidden relative group">
                          <img 
                            src={item.imageUrl} 
                            alt={item.name}
                            className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
                          />
                          {item.inventory === 0 && (
                            <div className="absolute inset-0 bg-black bg-opacity-50 flex items-center justify-center">
                              <Badge className="bg-red-500 text-white">Out of Stock</Badge>
                            </div>
                          )}
                        </div>
                      )}
                      <CardHeader>
                        <div className="flex items-start justify-between">
                          <CardTitle className="text-lg text-primary pr-2">{item.name}</CardTitle>
                          <Badge variant="outline" className="capitalize text-xs">
                            {item.category}
                          </Badge>
                        </div>
                      </CardHeader>
                      <CardContent>
                        <p className="text-gray-600 mb-4 text-sm line-clamp-2">{item.description}</p>

                        <div className="flex items-center justify-between mb-4">
                          <div className="text-2xl font-bold text-primary">
                            ${parseFloat(item.price).toFixed(2)}
                          </div>
                          <div className="flex items-center text-yellow-500">
                            <Star className="h-4 w-4 fill-current" />
                            <Star className="h-4 w-4 fill-current" />
                            <Star className="h-4 w-4 fill-current" />
                            <Star className="h-4 w-4 fill-current" />
                            <Star className="h-4 w-4 fill-current" />
                          </div>
                        </div>

                        {item.inventory !== null && item.inventory > 0 && (
                          <p className="text-sm text-gray-500 mb-4">
                            {item.inventory} in stock
                          </p>
                        )}

                        <div className="space-y-2">
                          {getCartItemCount(item.id) > 0 && (
                            <div className="bg-green-50 border border-green-200 rounded p-2 text-center">
                              <span className="text-green-800 text-sm font-medium">
                                {getCartItemCount(item.id)} in cart
                              </span>
                            </div>
                          )}

                          <Button 
                            onClick={() => addToCart(item.id)}
                            disabled={item.inventory === 0}
                            className="w-full bg-accent hover:bg-orange-600 disabled:bg-gray-300"
                            data-testid={`button-add-to-cart-${item.id}`}
                          >
                            {item.inventory === 0 ? (
                              "Out of Stock"
                            ) : (
                              <>
                                <ShoppingCart className="h-4 w-4 mr-2" />
                                Add to Cart
                              </>
                            )}
                          </Button>
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              )}

              {/* Empty State */}
              {merchandise && merchandise.length === 0 && (
                <div className="text-center py-16">
                  <Package className="h-24 w-24 text-gray-300 mx-auto mb-6" />
                  <h3 className="text-2xl font-semibold text-gray-600 mb-4">Shop Coming Soon</h3>
                  <p className="text-gray-500 mb-8 max-w-md mx-auto">
                    We're preparing amazing merchandise to help you support our mission. 
                    Check back soon for VOH-CBO branded items and more ways to show your support.
                  </p>
                  <div className="flex flex-col sm:flex-row gap-4 justify-center">
                    <Button className="bg-primary hover:bg-primary/90" data-testid="button-contact-shop">
                      Contact Us for Updates
                    </Button>
                    <Button variant="outline" data-testid="button-donate-instead">
                      <Heart className="h-4 w-4 mr-2" />
                      Donate Instead
                    </Button>
                  </div>
                </div>
              )}
            </>
          )}

          {/* Impact Message */}
          {merchandise && merchandise.length > 0 && (
            <div className="bg-gradient-to-br from-primary/10 to-secondary/10 rounded-2xl p-8 md:p-12 mb-16">
              <div className="text-center">
                <h2 className="font-serif text-3xl font-bold text-primary mb-4">Your Purchase Makes a Difference</h2>
                <p className="text-gray-600 leading-relaxed mb-6 max-w-2xl mx-auto">
                  Every item you purchase directly supports our programs and helps us continue empowering 
                  women and girls across Kenya. Your support creates lasting change in communities.
                </p>
                <div className="grid md:grid-cols-3 gap-6 max-w-4xl mx-auto">
                  <div className="text-center">
                    <div className="w-16 h-16 bg-accent rounded-full flex items-center justify-center mx-auto mb-3">
                      <Heart className="h-8 w-8 text-white" />
                    </div>
                    <h3 className="font-semibold text-primary mb-2">Funds Programs</h3>
                    <p className="text-sm text-gray-600">
                      Supports education, leadership training, and community initiatives
                    </p>
                  </div>
                  <div className="text-center">
                    <div className="w-16 h-16 bg-secondary rounded-full flex items-center justify-center mx-auto mb-3">
                      <Star className="h-8 w-8 text-white" />
                    </div>
                    <h3 className="font-semibold text-primary mb-2">Quality Products</h3>
                    <p className="text-sm text-gray-600">
                      Ethically sourced, high-quality merchandise you can be proud to wear
                    </p>
                  </div>
                  <div className="text-center">
                    <div className="w-16 h-16 bg-success rounded-full flex items-center justify-center mx-auto mb-3">
                      <Package className="h-8 w-8 text-white" />
                    </div>
                    <h3 className="font-semibold text-primary mb-2">Spreads Awareness</h3>
                    <p className="text-sm text-gray-600">
                      Helps share our mission and inspire others to get involved
                    </p>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Call to Action */}
          {merchandise && merchandise.length > 0 && (
            <div className="bg-gradient-to-r from-primary to-secondary rounded-2xl p-12 text-white text-center">
              <h2 className="font-serif text-3xl font-bold mb-4">More Ways to Support</h2>
              <p className="text-xl opacity-90 mb-8 max-w-2xl mx-auto">
                Shopping is just one way to support our mission. Explore other ways to get involved 
                and make a lasting impact in communities across Kenya.
              </p>
              <div className="flex flex-col sm:flex-row gap-4 justify-center">
                <Button size="lg" className="bg-white text-primary hover:bg-gray-100" data-testid="button-donate-cta">
                  <Heart className="mr-2 h-5 w-5" />
                  Make a Donation
                </Button>
                <Button 
                  size="lg" 
                  variant="outline" 
                  className="border-white text-white hover:bg-white hover:text-primary"
                  data-testid="button-volunteer-cta"
                >
                  Volunteer With Us
                </Button>
              </div>
            </div>
          )}
        </div>
      </div>
    </Layout>
  );
}
